import React, { useEffect, useRef } from 'react';

const PayPal = ({ total }) => {
    const paypalRef = useRef(null); // Create a ref for the PayPal container

    useEffect(() => {
        if (paypalRef.current) {
            paypalRef.current.innerHTML = ''; // Clear any existing buttons
        }

        if (window.paypal) {
            window.paypal.Buttons({
                createOrder: (data, actions) => {
                    return actions.order.create({
                        purchase_units: [
                            {
                                amount: {
                                    value: total.toFixed(2), // Use the total amount
                                },
                            },
                        ],
                    });
                },
                onApprove: (data, actions) => {
                    return actions.order.capture().then((details) => {
                        alert(`Transaction completed by ${details.payer.name.given_name}`);
                        console.log('Transaction details:', details);
                    });
                },
                onError: (err) => {
                    console.error('PayPal error:', err);
                },
            }).render(paypalRef.current); // Render into the ref container
        }
    }, [total]); // Re-run the effect only if `total` changes

    return <div ref={paypalRef}></div>; // Attach the ref to the container
};

export default PayPal;
