import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { useCart } from '../cartContext';
import heart from "../img/icon/heart.png";
import compare from "../img/icon/compare.png";
import search from "../img/icon/search.png";

const ShopSection = () => {
    const [products, setProducts] = useState([]);
    const location = useLocation();
    const navigate = useNavigate();
    const { addToCart } = useCart();
    const isKhmer = location.pathname.startsWith('/kh');
    const currentLanguage = location.pathname.split('/')[1] || 'en';
    const [categoryCounts, setCategoryCounts] = useState({});
    const [currentCategory, setCurrentCategory] = useState('');
    const [currentPage, setCurrentPage] = useState(1);
    const productsPerPage = 12;

    const categories = [
        { key: "men's clothing", en: "Men's Clothing", kh: "សម្លៀកបំពាក់បុរស" },
        { key: "women's clothing", en: "Women's Clothing", kh: "សម្លៀកបំពាក់ស្រី" },
        { key: "jewelery", en: "Jewelery", kh: "គ្រឿងអលង្ការ" },
        { key: "electronics", en: "Electronics", kh: "ឧបករណ៍អេឡិចត្រូនិច" },
    ];

    const handleAddToCart = (product) => {
        addToCart(product);
    };

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const url = currentCategory
                    ? `https://fakestoreapi.com/products/category/${currentCategory}`
                    : "https://fakestoreapi.com/products";
                const response = await fetch(url);
                const data = await response.json();
                setProducts(data);
            } catch (error) {
                console.error("Error fetching products:", error);
            }
        };

        const fetchCategoryCounts = async () => {
            const counts = {};
            for (const category of categories) {
                try {
                    const response = await fetch(`https://fakestoreapi.com/products/category/${category.key}`);
                    const data = await response.json();
                    counts[category.key] = data.length;
                } catch (error) {
                    console.error(`Error fetching category ${category.key}:`, error);
                }
            }
            setCategoryCounts(counts);
        };

        fetchCategoryCounts();
        fetchProducts();
    }, [currentCategory]);

    const indexOfLastProduct = currentPage * productsPerPage;
    const indexOfFirstProduct = indexOfLastProduct - productsPerPage;
    const currentProducts = products.slice(indexOfFirstProduct, indexOfLastProduct);

    const totalPages = Math.ceil(products.length / productsPerPage);

    const handlePageChange = (pageNumber) => {
        setCurrentPage(pageNumber);
    };

    const handleCategoryClick = (category) => {
        setCurrentCategory(category);
        setCurrentPage(1);
    };

    const handleSearchClick = (product) => {
        navigate(`/${currentLanguage}/shop-details`, { state: { product } });
    };

    return (
        <div>
            {/* Shop Section Begin */}
            <section className="shop spad">
                <div className="container">
                    <div className="row">
                        <div className="col-lg-3">
                            <div className="shop__sidebar">
                                <div className="shop__sidebar__accordion">
                                    <div className="accordion" id="accordionExample">
                                        <div className="card">
                                            <div className="card-heading">
                                                <a data-toggle="collapse" data-target="#collapseOne">{isKhmer ? 'ប្រភេទទំនិញ' : 'Categories'}</a>
                                            </div>
                                            <div id="collapseOne" className="collapse show" data-parent="#accordionExample">
                                                <div className="card-body">
                                                    <div className="shop__sidebar__categories">
                                                        <ul className="nice-scroll">
                                                            {categories.map((category) => (
                                                                <li key={category.key}>
                                                                    <a
                                                                        href="#!"
                                                                        onClick={(event) => {
                                                                            event.preventDefault();
                                                                            handleCategoryClick(category.key);
                                                                        }}
                                                                        className={currentCategory === category.key ? 'active-category' : ''}>
                                                                        {isKhmer ? category.kh : category.en} ({categoryCounts[category.key] || 0})
                                                                    </a>
                                                                </li>
                                                            ))}
                                                        </ul>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="col-lg-9">
                            <div className="row">
                                {currentProducts.map((product) => (
                                    <div key={product.id} className="col-lg-4 col-md-6 col-sm-6">
                                        <div className="product__item">
                                            <div
                                                className="product__item__pic"
                                                style={{
                                                    backgroundImage: `url(${product?.image})`,
                                                    backgroundSize: 'contain',
                                                    backgroundPosition: 'center',
                                                    height: '200px',
                                                    backgroundRepeat: 'no-repeat',
                                                }}>
                                                <ul className="product__hover">
                                                    <li><a href="#!"><img src={heart} alt="Heart" /></a></li>
                                                    <li><a href="#!"><img src={compare} alt="Compare" /> <span>{isKhmer ? 'ប្រៀបធៀប' : 'Compare'}</span></a></li>
                                                    <li>
                                                        <a href="#!" onClick={(event) => {
                                                            event.preventDefault();
                                                            handleSearchClick(product);
                                                        }}>
                                                            <img src={search} alt="Search" style={{ cursor: 'pointer' }} /><span>{isKhmer ? 'ព័ត៌មានលម្អិត' : 'View details'}</span>
                                                        </a>
                                                    </li>
                                                </ul>
                                            </div>
                                            <div className="product__item__text">
                                                <h6>{product?.title}</h6>
                                                <a style={{ cursor: 'pointer' }} onClick={() => handleAddToCart(product)} className="add-cart">+ Add To Cart</a>
                                                <div className="rating">
                                                    <i className="fa fa-star-o"></i>
                                                    <i className="fa fa-star-o"></i>
                                                    <i className="fa fa-star-o"></i>
                                                    <i className="fa fa-star-o"></i>
                                                    <i className="fa fa-star-o"></i>
                                                </div>
                                                <h5>${product?.price}</h5>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </div>
    );
};

export default ShopSection;
