import React,{useEffect} from 'react'
import { useLocation } from 'react-router-dom'
import instagram1 from '../img/instagram/instagram-1.jpg'
import instagram2 from '../img/instagram/instagram-2.jpg'
import instagram3 from '../img/instagram/instagram-3.jpg'
import instagram4 from '../img/instagram/instagram-4.jpg'
import instagram5 from '../img/instagram/instagram-5.jpg'
import instagram6 from '../img/instagram/instagram-6.jpg'

const Instagram = () => {
    const location = useLocation();
    const isKhmer = location.pathname.startsWith('/kh');

    useEffect(() => {
        document.querySelectorAll('.set-bg').forEach((element) => {
            const bg = element.getAttribute('data-setbg');
            element.style.backgroundImage = `url(${bg})`;
        });
    }, []);

  return (
    <div>
          {/* <!-- Instagram Section Begin --> */}
          <section class="instagram spad">
              <div class="container">
                  <div class="row">
                      <div class="col-lg-8">
                          <div class="instagram__pic">
                              <div class="instagram__pic__item set-bg" data-setbg={instagram1}></div>
                              <div class="instagram__pic__item set-bg" data-setbg={instagram2}></div>
                              <div class="instagram__pic__item set-bg" data-setbg={instagram3}></div>
                              <div class="instagram__pic__item set-bg" data-setbg={instagram4}></div>
                              <div class="instagram__pic__item set-bg" data-setbg={instagram5}></div>
                              <div class="instagram__pic__item set-bg" data-setbg={instagram6}></div>
                          </div>
                      </div>
                      <div class="col-lg-4">
                          <div class="instagram__text">
                              <h2>Instagram</h2>
                              <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut
                                  labore et dolore magna aliqua.</p>
                              <h3>{isKhmer ? '#សម្លៀកបំពាក់នារីទាន់សម័យ' : '#Female_Fashion'}</h3>
                          </div>
                      </div>
                  </div>
              </div>
          </section>
          {/* <!-- Instagram Section End --> */}
    </div>
  )
}

export default Instagram
