                        <div className="col-lg-12">
                            <ul className="filter__controls">
                                {categories.map((category) => (
                                    <li
                                        key={category.name}
                                        className={activeFilter === category.name ? 'active' : ''}
                                        onClick={() => handleFilterClick(category.name)}
                                    >
                                        {isKhmer ? category.khmerName : category.name}
                                    </li>
                                ))}
                            </ul>
                        </div>
                    </div>
                    <div className="row product__filter">
                        {/* Render Default Category Products */}
                        {activeFilter === 'electronics' &&
                            defaultCategoryProducts.map((product) => (
                                <div key={product.id} className="col-lg-3 col-md-6 col-sm-6 mix">
                                    <div className="product__item">
                                        <div
                                            className="product__item__pic set-bg"
                                            style={{
                                                backgroundImage: `url(${product.image})`,
                                                backgroundSize: 'contain',
                                                backgroundRepeat: 'no-repeat',
                                                backgroundPosition: 'center',
                                                height: '200px',
                                                width: '100%',
                                            }}
                                        >
                                            <ul className="product__hover">
                                                <li>
                                                    <a href="#">
                                                        <img src={heart} alt="Heart Icon" />
                                                    </a>
                                                </li>
                                                <li>
                                                    <a href="#">
                                                        <img src={compare} alt="Compare Icon" />
                                                        <span>Compare</span>
                                                    </a>
                                                </li>
                                                <li>
                                                    <a href="#">
                                                        <img src={search} alt="Search Icon" />
                                                    </a>
                                                </li>
                                            </ul>
                                        </div>
                                        <div className="product__item__text">
                                            <h6>{product.title}</h6>
                                            <a href="#" className="add-cart">
                                                + Add To Cart
                                            </a>
                                            <div className="rating">
                                                <i className="fa fa-star-o"></i>
                                                <i className="fa fa-star-o"></i>
                                                <i className="fa fa-star-o"></i>
                                                <i className="fa fa-star-o"></i>
                                                <i className="fa fa-star-o"></i>
                                            </div>
                                            <h5>${product.price}</h5>
                                        </div>
                                    </div>
                                </div>
                            ))}

                        {/* Render Products from Other Categories */}
                        {products
                            .filter((categoryData) => activeFilter === categoryData.category)
                            .map((categoryData, index) =>
                                categoryData.products.map((product) => (
                                    <div key={product.id} className="col-lg-3 col-md-6 col-sm-6 mix">
                                        <div className="product__item">
                                            <div
                                                className="product__item__pic set-bg"
                                                style={{
                                                    backgroundImage: `url(${product.image})`,
                                                    backgroundSize: 'contain',
                                                    backgroundRepeat: 'no-repeat',
                                                    backgroundPosition: 'center',
                                                    height: '200px',
                                                    width: '100%',
                                                }}
                                            >
                                                <ul className="product__hover">
                                                    <li>
                                                        <a href="#">
                                                            <img src={heart} alt="Heart Icon" />
                                                        </a>
                                                    </li>
                                                    <li>
                                                        <a href="#">
                                                            <img src={compare} alt="Compare Icon" />
                                                            <span>Compare</span>
                                                        </a>
                                                    </li>
                                                    <li>
                                                        <a href="#">
                                                            <img src={search} alt="Search Icon" />
                                                        </a>
                                                    </li>
                                                </ul>
                                            </div>
                                            <div className="product__item__text">
                                                <h6>{product.title}</h6>
                                                <a href="#" className="add-cart">
                                                    + Add To Cart
                                                </a>
                                                <div className="rating">
                                                    <i className="fa fa-star-o"></i>
                                                    <i className="fa fa-star-o"></i>
                                                    <i className="fa fa-star-o"></i>
                                                    <i className="fa fa-star-o"></i>
                                                    <i className="fa fa-star-o"></i>
                                                </div>
                                                <h5>${product.price}</h5>
                                            </div>
                                        </div>
                                    </div>
                                ))
                            )}
                    </div>
                </div>
            </section>
        </div>
    );
}

export default Product
