import React, {useEffect, useState} from 'react'
import { useLocation } from 'react-router-dom'
import calender from "../img/icon/calendar.png"
import blog1 from "../img/blog/blog-1.jpg"
import blog2 from "../img/blog/blog-2.jpg"
import blog3 from "../img/blog/blog-3.jpg"

const Blog = () => {
    const location = useLocation();
    const isKhmer = location.pathname.startsWith('/kh');
    const [products, setProducts] = useState([]);
    const currentLanguage = location.pathname.split('/')[1] || 'en';

    // useEffect(() => {
    //     document.querySelectorAll('.set-bg').forEach((element) => {
    //         const bg = element.getAttribute('data-setbg');
    //         element.style.backgroundImage = `url(${bg})`;
    //     });
    // }, []);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await fetch("https://fakestoreapi.com/products/category/men's clothing");
                const data = await response.json();
                setProducts(data.slice(0, 3));
            } catch (error) { console.error("Error fetching products:", error); }
        };

        fetchProducts();
    }, []);

  return (
    <div>
          {/* <!-- Latest Blog Section Begin --> */}
          <section class="latest spad">
              <div class="container">
                  <div class="row">
                      <div class="col-lg-12">
                          <div class="section-title">
                              <span>{isKhmer ? 'ព័ត៍មានចុងក្រោយ' : 'Latest News'}</span>
                              <h2>{isKhmer ? 'ម៉ូតសម្លៀកបំពាក់ថ្មី' : 'Fashion New Trends'}</h2>
                          </div>
                      </div>
                  </div>
                  <div class="row">
                        <div  class="col-lg-4 col-md-6 col-sm-6">
                           {products.map((product, index) => {
                              <div key={index} class="blog__item">
                                    <div class="blog__item__pic set-bg">
                                        <img src={product.image} alt="" />
                                    </div>
                                    <div class="blog__item__text">
                                        <span><img src={calender} alt="" /> 16 February 2020</span>
                                        <h5>What Curling Irons Are The Best Ones</h5>
                                        <Link to={`/${currentLanguage}/shop`}>Buy Now</Link>
                                    </div>
                                </div>
                            })}
                        </div>
                      {/* <div class="col-lg-4 col-md-6 col-sm-6">
                          <div class="blog__item">
                              <div class="blog__item__pic set-bg" data-setbg={blog2}></div>
                              <div class="blog__item__text">
                                  <span><img src={calender} alt="" /> 21 February 2020</span>
                                  <h5>Eternity Bands Do Last Forever</h5>
                                  <a href="#">Read More</a>
                              </div>
                          </div>
                      </div>
                      <div class="col-lg-4 col-md-6 col-sm-6">
                          <div class="blog__item">
                              <div class="blog__item__pic set-bg" data-setbg={blog3}></div>
                              <div class="blog__item__text">
                                  <span><img src={calender} alt="" /> 28 February 2020</span>
                                  <h5>The Health Benefits Of Sunglasses</h5>
                                  <a href="#">Read More</a>
                              </div>
                          </div>
                      </div> */}
                  </div>
              </div>
          </section>
          {/* <!-- Latest Blog Section End --> */}
    </div>
  )
}

export default Blog
